import React, { useState } from 'react'
import './MockSSO.scss'
import LogoLinear from "src/assets/images/logo-linear.svg";
import { Button, Col, Row } from 'antd';
import { useNavigate } from 'react-router-dom';
import { baseURL } from '../../config'

export default function MockSSO() {
    // admin 00306160
    const assertion = ""

    // participant 123
    const assertionParticipant = ""

    const assertionUnauthorized = ""

    const [showMock, setShowMock] = useState(false)
    const navigate = useNavigate()
    const returnUrl = `${window.location.origin}/layout/dashboard`
    const callbackUrl = `${baseURL}auth/callback?redirect_uri=${encodeURIComponent(returnUrl)}`

    return (
        <div className="login-page-bg-image">
            <div className="login-full-page">
                <div >
                    <img className="login-img-fluid" src={LogoLinear} alt='icon' />
                </div>
                {showMock ? (
                    <Row justify={'center'}>
                        <Col xs={20} sm={20} md={20} lg={20} xl={8} >
                            <div className="login-page-content">
                                <div>
                                    <div>Mock SSO (admin)</div>
                                </div>
                                <form action={callbackUrl} method="post">
                                    <div className="login-page-content-form">
                                        <textarea style={{width: '100%', height: 80}} name="SAMLResponse" placeholder="SAMLResponse" defaultValue={assertion} />
                                        <input type="hidden" name="RelayState" value={returnUrl} />
                                        <input type="submit" value="Login" />
                                    </div>
                                </form>
                                <br />
                                <div>
                                    <div>Mock SSO (participant)</div>
                                </div>
                                <form action={callbackUrl} method="post">
                                    <div className="login-page-content-form">
                                        <textarea style={{width: '100%', height: 80}} name="SAMLResponse" placeholder="SAMLResponse" defaultValue={assertionParticipant} />
                                        <input type="hidden" name="RelayState" value={returnUrl} />
                                        <input type="submit" value="Login" />
                                    </div>
                                </form>
                                <br />
                                <div>
                                    <div>Mock SSO (not authorized)</div>
                                </div>
                                <form action={callbackUrl} method="post">
                                    <div className="login-page-content-form">
                                        <textarea style={{width: '100%', height: 80}} name="SAMLResponse" placeholder="SAMLResponse" defaultValue={assertionUnauthorized} />
                                        <input type="hidden" name="RelayState" value={returnUrl} />
                                        <input type="submit" value="Login" />
                                    </div>
                                </form>
                                <br />
                                <Button type="link" onClick={() => setShowMock(false)}>
                                    Back to Register / Login
                                </Button>
                            </div>
                        </Col>
                    </Row>
                ) : (
                    <Row justify={'center'}>
                        <Col xs={20} sm={20} md={20} lg={20} xl={8} >
                            <div className="login-page-content">
                                <div className="login-page-content-h2">Welcome</div>
                                <div className="login-page-content-h5">Register or log in to continue to Mock SSO</div>
                                <div className="login-page-content-form">
                                    <Button
                                        className="login-button"
                                        block
                                        type="primary"
                                        style={{ marginBottom: 16 }}
                                        onClick={() => setShowMock(true)}
                                    >
                                        Log in
                                    </Button>
                                    <Button
                                        block
                                        onClick={() => navigate('/register')}
                                    >
                                        Register
                                    </Button>
                                </div>
                            </div>
                        </Col>
                    </Row>
                )}
            </div>
        </div>
    )
}
